dataset = ClassDataset()
d_train, d_val, d_test = data.random_split(dataset, [0.5, 0.3, 0.2])

train_data = data.DataLoader(d_train, batch_size = 32, shuffle=True, drop_last=True)
train_data_val = data.DataLoader(d_val, batch_size = 50, shuffle=False, drop_last=False)
test_data = data.DataLoader(d_test, batch_size = 20, shuffle=False, drop_last=False)
